package view;

public class Camera {
	
	private static int WIDTH = 20;
	private static int HEIGHT = 20;
	private int x = 0;
	private int y = 0;

	public Camera() {
		// TODO Auto-generated constructor stub
	}

	public int getWIDTH() {
		return this.WIDTH;
	}

	public void setWIDTH(int WIDTH) {
		this.WIDTH = WIDTH;
	}

	public int getHEIGHT() {
		return this.HEIGHT;
	}

	public void setHEIGHT(int hEIGHT) {
		this.HEIGHT = hEIGHT;
	}

	public int getY() {
		return this.y;
	}

	public void setY(int y) {
		this.y = y;
	}

	public int getX() {
		return this.x;
	}

	public void setX(int x) {
		this.x = x;
	}

}
